™qta<?php exit; ?>a:1:{s:7:"content";a:14:{s:37:"rx_theme_assistant_enable_breadcrumbs";a:1:{i:0;s:4:"true";}s:30:"rx_theme_assistant_page_header";a:1:{i:0;s:7:"default";}s:30:"rx_theme_assistant_page_footer";a:1:{i:0;s:7:"default";}s:20:"_elementor_edit_mode";a:1:{i:0;s:7:"builder";}s:24:"_elementor_template_type";a:1:{i:0;s:4:"post";}s:17:"_wp_page_template";a:1:{i:0;s:7:"default";}s:15:"_elementor_data";a:1:{i:0;s:12736:"[{"id":"5a98","elType":"section","settings":{"gap":"no","layout":"full_width","rx_parallax_layout_list":[{"rx_parallax_layout_image":{"url":"","id":""},"rx_parallax_layout_type":"scroll","rx_parallax_layout_speed":{"unit":"%","size":50},"rx_parallax_layout_bg_size":"auto","rx_parallax_layout_animation_prop":"transform","rx_parallax_layout_bg_x":50,"rx_parallax_layout_bg_y":50,"rx_parallax_layout_z_index":"","rx_parallax_layout_on":["desktop","tablet"],"_id":"f94dce4","rx_parallax_layout_visible":["desktop","tablet","mobile"]}],"padding":{"unit":"px","top":"50","right":"0","bottom":"50","left":"0","isLinked":false}},"elements":[{"id":"6c7b","elType":"column","settings":{"_column_size":100,"content_position":"top"},"elements":[{"id":"3687","elType":"widget","settings":{"title":"Who we are","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"9b4","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">We are the company which specialises on Web Development<\/span><\/p><p><span style=\"font-weight: 400;\">Our contact information: website address is <a href=\"http:\/\/rovadex.com\">rovadex.com<\/a>, e-mail is\u00a0<a href=\"mailto:rovadex.team@gmail.com\">rovadex.team@gmail.com<\/a><\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"29dd","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"5952","elType":"widget","settings":{"title":"Personal data we collect","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"3423","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">By default WordPress based theme does not collect any personal data about visitors, and only collects the data shown on the User Profile screen from registered users. <\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"d20","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"6d9","elType":"widget","settings":{"title":"Comments","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"389e","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">In comments left by users or visitors on the site we collect the data shown in the comments form, and also the visitor\u2019s IP address and browser user agent string to help spam detection.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"6f7f","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"5b4c","elType":"widget","settings":{"title":"Media","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"3795","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">Users and Visitors to the website can download and extract any location data from images on the website. If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. <\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"40aa","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"49b","elType":"widget","settings":{"title":"Contact Forms","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"6038","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">Before information submission we've got explicit consent from users to store their information.<\/span><span style=\"font-weight: 400;\">We never use such data for marketing purposes or share with third parties.<\/span><span style=\"font-weight: 400;\">We comply with data-deletion in case of requests. <\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"3811","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"3d76","elType":"widget","settings":{"title":"Cookies","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"4f77","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">Our WordPress theme use cookies so if the user or visitor leave a comment, thay may opt-in to saving their name, email address and website in cookies. Third party cookies are those placed on your device by third party website domains. This includes social sharing, videos and other services we offer that are run by other companies. You can view information about what some of these sites do with your information here:<\/span><\/p><ul><li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Facebook<\/span><\/li><li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">YouTube<\/span><\/li><li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Twitter<\/span><\/li><li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Instagram<\/span><\/li><\/ul>"},"elements":[],"widgetType":"text-editor"},{"id":"47e4","elType":"widget","settings":{"space":{"unit":"px","size":20}},"elements":[],"widgetType":"spacer"},{"id":"444d","elType":"widget","settings":{"title":"Analytics","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"3b02","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">By default WordPress does not collect any analytics data. However, many web hosting accounts collect some anonymous analytics data. You may also have installed a WordPress plugin that provides analytics services.<\/span><\/p><p><span style=\"font-weight: 400;\">User or site administrator may use Google Analytics to help analyse how visitors use this site. Google Analytics uses \u201ccookies\u201d, which are small text files placed on your computer, to collect standard internet log information and visitor behaviour information in an anonymous form.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"6c13","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"7e50","elType":"widget","settings":{"title":"Embedded content from other websites","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"5849","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">The content in your theme may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.<\/span><\/p><p><span style=\"font-weight: 400;\">These websites may collect data about you, use cookies, embed additional third-party tracking and monitor your interaction with that embedded content, including tracing your interaction with the embedded content if you have an account and are logged in to that website.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"54e","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"366f","elType":"widget","settings":{"title":"Data retain","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"2f7c","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">In cases of commenting we will retain the data for the statutory minimum required period.<\/span><\/p><p><span style=\"font-weight: 400;\">In cases of registration we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"3005","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"285a","elType":"widget","settings":{"title":"Who we share your data with","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"4a8b","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">We do not share any of your information by default \u00a0also we don\u2019t share your personal data with third-parties who intend to use the data for marketing purposes if you have not given your consent to this.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"4768","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"4781","elType":"widget","settings":{"title":"What rights you have over your data","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"7026","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">For the user or visitor on the website there\u2019s a possibility to register or leave a comment.<\/span><\/p><p><span style=\"font-weight: 400;\">Also there's an opportunity to to receive by request an exported file of the personal data we hold about you, including any data you have provided to us. <\/span><\/p><p><span style=\"font-weight: 400;\">We do not able to erase data that we hold about you when user being an active account. Ensuring accuracy of information, user can also request corrections. So by request we can perform the erasing.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"467","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"7214","elType":"widget","settings":{"title":"Where we send your data","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"5abd","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">We do not make any daily backups by default. Only user and visitor comments may be checked through an automated spam detection service.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"68b2","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"3","elType":"widget","settings":{"title":"Additional information","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"1b80","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">We are a part of the services that we provide, and we are given a variety of login details:<\/span><\/p><p><span style=\"font-weight: 400;\">WordPress accounts <\/span><\/p><p><span style=\"font-weight: 400;\">FTP \/ Database Access<\/span><\/p><p><span style=\"font-weight: 400;\">Payments Gateways (Payoneer \/ PayPal)<\/span><\/p><p><span style=\"font-weight: 400;\">Web Hosting \/ Control Panels \/ Domains<\/span><\/p><p><span style=\"font-weight: 400;\">Social Media Accounts (E.G. Facebook \/ Twitter \/ Instagram \/ LinkedIn etc)<\/span><\/p><p><span style=\"font-weight: 400;\">Google Services (E.G. Analytics \/ AdWords \/ YouTube \/ Search Console etc)<\/span><\/p><p><span style=\"font-weight: 400;\">Email and Email Marketing Accounts<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"65b9","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"5dfe","elType":"widget","settings":{"title":"How we protect your data","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"195f","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">Our main goal is to protect user\u2019s personally identifying information (PII) and hold businesses to a higher standard.<\/span><\/p><p><span style=\"font-weight: 400;\">All the plugins that we used are all up to date, and we use the latest version of Wordpress. We encrypt any personal information and our user communications with the servers with the help of SSL\/HTTPS protocol. All administrative credentials are unique, any data breach issues will be immediately under resolving process.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"6fac","elType":"widget","settings":{"space":{"unit":"px","size":30}},"elements":[],"widgetType":"spacer"},{"id":"2475","elType":"widget","settings":{"title":"What third parties we receive data from","header_size":"h3"},"elements":[],"widgetType":"heading"},{"id":"5c0a","elType":"widget","settings":{"editor":"<p><span style=\"font-weight: 400;\">In case of third party links posting we are not perform any checking activities related to security or privacy compliance.<\/span><\/p><p><span style=\"font-weight: 400;\">We are not responsible for information collected or used by any advertiser or third-party site.<\/span><\/p><p><span style=\"font-weight: 400;\">All social media sharing links, either displayed as text links or social media icons do not connect you to any of the associated third parties, unless you explicitly click on them.<\/span><\/p>"},"elements":[],"widgetType":"text-editor"},{"id":"aa0a102","elType":"widget","settings":{"space":{"unit":"px","size":60}},"elements":[],"widgetType":"spacer"}],"isInner":false}],"isInner":false}]";}s:18:"_elementor_version";a:1:{i:0;s:5:"2.6.8";}s:14:"slide_template";a:1:{i:0;s:7:"default";}s:21:"_wxr_import_user_slug";a:1:{i:0;s:12:"rovadex_demo";}s:14:"_elementor_css";a:1:{i:0;s:135:"a:6:{s:4:"time";i:1634131253;s:5:"fonts";a:0:{}s:5:"icons";a:0:{}s:20:"dynamic_elements_ids";a:0:{}s:6:"status";s:4:"file";i:0;s:0:"";}";}s:21:"_wp_trash_meta_status";a:1:{i:0;s:7:"publish";}s:19:"_wp_trash_meta_time";a:1:{i:0;s:10:"1634319674";}s:21:"_wp_desired_post_slug";a:1:{i:0;s:14:"privacy-policy";}}}